mapper.update:

UPDATE data_auth set
  name=#{name}
where 1=1
and data_id = #{dataId}
and type = #{type}

mapper.select:
SELECT t.id, t.type, t.data_id, t.`name`, t.parent_data_id FROM ( SELECT d.id, d.type, d.data_id, d.`name`, d.parent_data_id FROM data_auth d INNER JOIN user_data_auth ud ON d.id = ud.data_auth_id WHERE 1=1
AND d.data_id = #{dataId}
AND d.type = #{type}
AND ud.user_id = #{userId}
UNION SELECT d.id, d.type, d.data_id, d.`name`, d.parent_data_id FROM data_auth d RIGHT JOIN role_data_auth rd ON d.id = rd.data_auth_id RIGHT JOIN user_role ur ON ur.role_id = rd.role_id WHERE 1=1
AND d.data_id = #{dataId}
AND d.type = #{type}
AND ur.user_id = #{userId}
) t WHERE t.id IS NOT NULL
